<?phpnamespace Api;class MapApiQueryBuilder{    public $ch;    public function __construct()    {        $this->ch = curl_init();    }    public function getResponce($url,$options)    {        curl_setopt($this->ch, CURLOPT_RETURNTRANSFER, 1);        curl_setopt($this->ch, CURLOPT_URL, $url . '?' . http_build_query($options));        $responce = curl_exec($this->ch);        return json_decode($responce);    }    public function __destruct()    {        curl_close($this->ch);    }}