<?phprequire_once dirname(__DIR__) . '/functions/functions.php';$data = json_decode(file_get_contents("php://input"));$validation = validate($data);if ($validation["code"]) {    $url = '';    $options = array();    $myApiKey = 'pk.7765f037c4986a841a7c32297fbc28fa';    if ($data->region === 'address') {        $url = 'https://us1.locationiq.com/v1/search.php';        $options = array(            'key' => $myApiKey,            'q' => $data->address,            'format' => 'json'        );    } else {        $url = 'https://us1.locationiq.com/v1/reverse.php';        $options = array(            'key' => $myApiKey,            'lat' => $data->lat,            'lon' => $data->lon,            "format" => "json"        );    }    $ch = curl_init();    curl_setopt($ch, CURLOPT_RETURNTRANSFER, 1);    curl_setopt($ch, CURLOPT_URL, $url . '?' . http_build_query($options));    $responce = curl_exec($ch);    $dataResponce = json_decode($responce);    curl_close($ch);    debug(is_array($dataResponce) ? $dataResponce : $dataResponce->address);} else {   echo $validation['message'];}