<?phpfunction validate($data){    if ($data->region === 'address') {        if (!empty($data->address)) {            return ["message" => "Validation success","code" => "1"] ;        } else {            return  ["message" => "Validation error","code" => "0"] ;        }    } else {        if ($data->lat && $data->lon) {            return ["message" => "Validation success","code" => "1"] ;        } else {            return  ["message" => "Validation error","code" => "0"] ;        }    }}function debug($array){    echo "<pre>" . print_r($array, 1) . "</pre>";}