<?phpnamespace Objects\Maps;use Objects\Maps\SetOptionInterface;class Coordinate implements SetOptionInterface{    protected $url = 'https://us1.locationiq.com/v1/reverse.php';    public function getUrl()    {        return $this->url;    }    public function options($data)    {        if ($data->lat && $data->lon) {            return ['lat' => $data->lat,                    'lon' => $data->lon];        }        return null;    }}