<?phpnamespace Objects\Maps;use objects\Maps\SetOptionInterface;class Maps{    protected $apiKey;    protected $format;    protected $data;    protected $region;    public function __construct($apiKey, $data, SetOptionInterface $region, $format = 'json')    {        $this->apiKey = $apiKey;        $this->data = $data;        $this->format = $format;        $this->region = new $region();    }    public function options()    {        $options = ['key' => $this->apiKey];        $data = $this->region->options($this->data);        foreach ($data as $k => $v) {            $options[$k] = $v;        }        $options['format'] = $this->format;        return $options;    }    public function url()    {        return $this->region->getUrl();    }}